package com.jason;/** * @author 王政 * @date 2022/5/19 11:43 */public class MainApplication {    public static void main(String[] args) throws InterruptedException {        Thread t1 = new Thread(() -> {            for (int i = 1; i < 4; i++) {                System.out.println("线程A ： " + i);            }        });        Thread t2 = new Thread(() -> {            for (int i = 1; i < 4; i++) {                System.out.println("线程B ： " + i);            }        });        t1.start();        t2.start();        t1.join();        t2.join();        System.out.println("爬取结束");    }}